const sceneVars = runtimeScene.getVariables();
const possibleInputs = [inputsMatrice.KEYBOARD.a, inputsMatrice.KEYBOARD.b, inputsMatrice.KEYBOARD.e, inputsMatrice.KEYBOARD.r, inputsMatrice.KEYBOARD.v, inputsMatrice.KEYBOARD.z];

const lastCheck = sceneVars.get('timerCheck').getAsNumber();
if (lastCheck === 0) {
    sceneVars.get('timerCheck').setNumber(Date.now());
}

const currentSuccessQTE = sceneVars.get('currentSuccessQTE');
const trapDifficulty = sceneVars.get('trapDifficulty');
// console.log('currentQTE ', currentSuccessQTE.getValue(), ' >= ', trapDifficulty.getValue());
if (currentSuccessQTE.getValue() >= trapDifficulty.getValue()) {
    // QTE is over !
    currentSuccessQTE.setNumber(0);
    trapDifficulty.add(1);
    sceneVars.get('trapped').setValue(2);
    return;
}

let expectedInput = sceneVars.get('qteExpectedInput');
if (possibleInputs.indexOf(expectedInput.getAsNumber()) === -1) {
    const expectedInputIndex = Math.random() * possibleInputs.length >> 0;
    // console.log('get a new expectedInput, current value is', expectedInput.getAsNumber());
    expectedInput.setNumber(possibleInputs[expectedInputIndex]);
    runtimeScene.getObjects('QTE')[0].setAnimation(expectedInputIndex);

    // create the visual renderer of the expectedInput
    return;
}
// console.log('Expected input is ', expectedInput.getValue());
// console.log('last check', lastCheck - Date.now());
if (Date.now() - lastCheck > 250) {
    const lastKeyPressed = runtimeScene.getGame().getInputManager().getLastPressedKey();
    // console.log('Last key pressed is ', lastKeyPressed);

    if (runtimeScene.getGame().getInputManager().anyKeyPressed()) {
        sceneVars.get('timerCheck').setNumber(Date.now());
        if (lastKeyPressed == expectedInput.getValue()) {
            // play sound success
            currentSuccessQTE.add(1);
            runtimeScene.getSoundManager().playSound('assets\SUCCESS_QTE.mp3');
            sceneVars.get('playSuccessQTE').setBoolean(true);
        } else {
            runtimeScene.getSoundManager().playSound('assets\FAIL_QTE.mp3');
            sceneVars.get('playFailQTE').setBoolean(true);
            // play sound error
        }
        expectedInput.setString('');
        runtimeScene.resetTimer('qteCheck');
    }
}

