const sceneVars = runtimeScene.getVariables();
const shouldSpawnPattern = sceneVars.get("shouldSpawnAPattern");
if (shouldSpawnPattern.getAsBoolean() === true) {
    shouldSpawnPattern.setBoolean(false);
    const scenes = runtimeScene.getGame().getGameData().layouts.filter(map => map.name.match('Pattern'));

    const lastPatternIndex = sceneVars.get("lastPatternIndex");
    let indexToLoad = Math.random() * (scenes.length - 1) >> 0;
    while (indexToLoad === lastPatternIndex.getValue()) {
        indexToLoad = Math.random() * (scenes.length - 1) >> 0;
    }
    lastPatternIndex.setValue(indexToLoad);
    const patternToLoad = scenes[indexToLoad];
    console.log('Spawning a pattern ', indexToLoad, ' - ', patternToLoad.name, ' - X instances: ', patternToLoad.instances.length)

    patternToLoad.instances.forEach((instanceData, index) => {
        const obj = runtimeScene.createObject(instanceData.name);
        obj.setX(instanceData.x + runtimeScene.getLayer("BaseLayer").getCameraWidth() / 2 + sceneVars.get("triggerPatternX").getValue());
        obj.setY(instanceData.y);
        if (instanceData.customSize) {
            obj.setWidth(instanceData.width);
            obj.setHeight(instanceData.height);
        }
        obj.setAngle(instanceData.angle);
        obj.setZOrder(instanceData.zOrder);
        obj.setLayer(instanceData.layer);
    });
    sceneVars.get("activateRandomizer").setBoolean(true);
}
